import "./App.css";
import Navbar from "./components/Navbar";
import Table from "./components/Table";
import Home from "./pages/Home";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import { useState, useEffect, useLayoutEffect } from "react";
import EditContact from "./pages/EditContact";
import Footer from "./components/Footer";
import About from "./pages/About";

function App() {
  const [contacts, setContacts] = useState([]);
  const [contact, setContact] = useState([]);
  const [navEditContact, setNavEditContact] = useState(false);

  useEffect(() => {
    const getContacts = async () => {
      const fetchedContacts = await fetchContacts();
      setContacts(fetchedContacts);
    };

    getContacts();
  }, []);

  // Fetch Contacts
  const fetchContacts = async () => {
    const res = await fetch("http://localhost:5000/contacts");
    const data = await res.json();
    return data;
  };

  const header = [
    "First Name",
    "Last Name",
    "Email",
    "Phone Number",
    "House/Yard",
    "Suite",
    "Class Year",
    "Home City",
    "Home State",
    "Contact Status",
    "PTE Status",
    "Registration Status",
    "Stage of Voting Process",
    "Where Registered",
    "Actions",
  ];

  // Edit Contact
  const editContact = (contact) => {
    setContact(contact);
    setNavEditContact(true);
  };

  // Save Contact
  const saveContact = (contact) => {
    setNavEditContact(false);
    console.log(contact);
  };

  // Delete Contact
  const deleteContact = (id) => {
    setContacts(contacts.filter((contact) => contact.id !== id));
  };

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route
          path="/"
          exact
          element={
            <>
              {navEditContact && <Navigate to="/edit-contact" />}
              {contacts.length > 0 ? <Home /> : <h3>No Contacts</h3>}
            </>
          }
        />
        <Route
          path="/edit-contact"
          element={
            navEditContact ? (
              <EditContact contact={contact} onSave={saveContact} />
            ) : (
              <Navigate to="/" />
            )
          }
        />
        <Route path="/about" element={<About />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
