import { useState, useCallback } from "react";
import "./SearchBar.css";

function SearchBar({ onContactsChange }) {
    const [query, setQuery] = useState("");

    // Update query state on input change
    const inputChange = useCallback(async (e) => {
        const { value } = e.target;
        setQuery(value);

        try {
            const response = await fetch(`http://localhost:8000/contacts/?query=${query}`);
            const parseResponse = await response.json();
            onContactsChange(parseResponse);
            console.log(parseResponse);
        } catch (error) {
            console.error(error.message);
        }

    }, [onContactsChange]);

    return (
        <>
            <input
                type="search"
                placeholder="Search"
                name="search"
                value={query}
                onChange={inputChange}
                className="form-control"
            />
        </>
    );
}

export default SearchBar;
