  //       console.log(err);
  //     });
  // };

  // Delete Contact
  const deleteContact = (id) => {
    if (window.confirm("Are you sure you want to delete this contact?")) {
      remove(ref(db, "contacts/" + id))
        .then(() => {
          console.log("Success!");
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route
          path="/"
          exact
          element={<Table onDelete={deleteContact} />}
        />
        <Route
          path="/edit-contact"
          element={<EditContact />}
        />
        <Route path="/about" element={<About />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
